struct point_t {
	double x, y;
	point_t operator+(const point_t &p) { return { x + p.x, y + p.y }; }
	point_t operator-(const point_t &p) { return { x - p.x, y - p.y }; }
	point_t operator*(const double &c) { return { x * c, y * c }; }
};

// ax + by + c = 0
struct line_t {
	double a, b, c;
};

point_t cross_point(line_t l, line_t m) {
	point_t ret = { INFD, INFD };
	double d = l.a * m.b - m.a * l.b;
	if (nearlyeq(d, 0)) return ret;
	double x = l.b * m.c - m.b * l.c;
	double y = l.a * m.c - m.a * l.c;
	ret = { x / d, y / -d };
	return ret;
}

// normal vector = (a, b), passing p
line_t solve_line(double a, double b, point_t p) {
	return { a, b, -a * p.x - b * p.y };
}

// t should be radius
point_t rot(point_t p, double r) {
	point_t ret = {
		{ cos(r) * p.x - sin(r) * p.y },
		{ sin(r) * p.x + cos(r) * p.y }
	};
	return ret;
}

double abs(point_t p) {
	return sqrt(p.x * p.x + p.y * p.y);
}

// angle [0, 2PI) of vector p to vector q
double angle(point_t p, point_t q) {
	p = p * (1.0 / abs(p));
	q = q * (1.0 / abs(q));
	double r0 = acos(p.x * q.x + p.y * q.y);
	double r1 = asin(p.x * q.y - p.y * q.x);
	if (r1 >= 0) return r0;
	else return 2 * M_PI - r0;
}

