bool on_same_line(intpt_t p, intpt_t q) {
	int mask = (p.x ? 0 : 0b1000) + (p.y ? 0 : 0b100)
		+ (q.x ? 0 : 0b10) + (q.y ? 0 : 0b1);
	if ((mask & 0b1100) == 0b1100 || (mask & 0b0011) == 0b0011) return true;
	if ((mask & 0b1010) == 0b1010 || (mask & 0b0101) == 0b0101) return true;
	if (mask == 0b0000) {
		p = p / gcd(p.x, p.y);
		q = q / gcd(q.x, q.y);
		if (p.x < 0) p = p * -1;
		if (q.x < 0) q = q * -1;
		if (p == q) return true;
		else return false;
	}
	else return false;
}

int main() {
	intpt_t p0, p1;
	cin >> p0.x >> p0.y >> p1.x >> p1.y;
	int q; cin >> q;
	cout << setprecision(20);
	Loop(i, q) {
		intpt_t p2;
		cin >> p2.x >> p2.y;
		intpt_t ip = p1 - p0;
		intpt_t iq = p2 - p0;
		pt_t p = cvtpt(ip);
		pt_t q = cvtpt(iq);
		bool judge;
		if (on_same_line(ip, iq)) judge = true;
		else judge = false;
		double r = angle(p, q);
		if (judge) {
			if (M_PI * 0.5 < r && r < M_PI * 1.5) {
				cout << "ONLINE_BACK" << endl;
			}
			else {
				if (abs(q) > abs(p)) {
					cout << "ONLINE_FRONT" << endl;
				}
				else {
					cout << "ON_SEGMENT" << endl;
				}
			}
		}
		else {
			if (r < M_PI) {
				cout << "COUNTER_CLOCKWISE" << endl;
			}
			else {
				cout << "CLOCKWISE" << endl;
			}
