string ccw(P a,P b,P c){
  P ab=b-a;
  P ac=c-a;
  P k=ac*conj(ab);
  if( k.y > eps ){
    return "COUNTER_CLOCKWISE";
  }
  if( k.y < -eps ){
    return "CLOCKWISE";
  }
  if( k.x < -eps ){
    return "ONLINE_BACK";
  }  
  if( abs(ab)+eps < abs(ac) ){
    return "ONLINE_FRONT";
  }
  return "ON_SEGMENT";
}

