class Point {
    public:
        double x, y;
        Point(double x = 0.0, double y = 0.0): x(x), y(y) {}

        Point operator + (Point p) { return Point(x + p.x, y + p.y); }
        Point operator - (Point p) { return Point(x - p.x, y - p.y); }
        Point operator * (double k) { return Point(x * k, y * k); }
        Point operator / (double k) { return Point(x / k, y / k); }

        double friend norm(Point p) { return p.x * p.x + p.y * p.y; }
        double friend abs(Point p) { return sqrt(norm(p)); }
};
typedef Point Vector;

double dot(Vector v1, Vector v2) {
    return v1.x * v2.x + v1.y * v2.y;
}

double cross(Vector v1, Vector v2) {
    return v1.x * v2.y - v1.y * v2.x;
}

int main(void) {
    int q, c, d;
    Point p0, p1, p2;
    cin >> p0.x >> p0.y;
    cin >> p1.x >> p1.y;
    cin >> q;

    for (int i = 0; i < q; i++) {
        cin >> p2.x >> p2.y;
        c = cross(p1 - p0, p2 - p0);
        d = dot(p1 - p0, p2 - p0);
        if (c > 0) cout << "COUNTER_CLOCKWISE\n";
        else if (c < 0) cout << "CLOCKWISE\n";
        else if (d < 0) cout << "ONLINE_BACK\n";
        else if (abs(p2 - p0) > abs(p1 - p0)) cout << "ONLINE_FRONT\n";
        else cout << "ON_SEGMENT\n";
    }

