				}
				else
				{
					cout << "ON_SEGMENT" << endl;
				}
			}
		}
		else if (st > EPS)
		{
			cout << "COUNTER_CLOCKWISE" << endl;
		}
		else
		{
			cout << "CLOCKWISE" << endl;
		}
	}

	return 0;
}

