    Line(Point p1 = Point(), Point p2 = Point()) : p1(p1), p2(p2) {}

    bool operator<(const Line &rhs) const {
        if (eq(p2, rhs.p2))
            return p1 < rhs.p1;
        return p2 < rhs.p2;
    }
    bool operator==(const Line &rhs) const {
        return (eq(p1, rhs.p1) && eq(p2, rhs.p2)) ||
               (eq(p1, rhs.p2) && eq(p2, rhs.p1));
    }
};

enum CCW {
    ONLINE_FRONT = -2,
    CLOCKWISE,
    ON_SEGMENT,
    COUNTER_CLOCKWISE,
    ONLINE_BACK
};

int ccw(Point p0, Point p1, Point p2){
    Point a = p1 - p0;
    Point b = p2 - p0;
    if(sgn(det(a, b)) == 1) return COUNTER_CLOCKWISE;
    if(sgn(det(a, b)) == -1)return CLOCKWISE;
    if(sgn(dot(a, b)) == -1)return ONLINE_BACK;
    if(sgn(norm(b)-norm(a)) == 1)return ONLINE_FRONT;
    return ON_SEGMENT;
}

