double dot(Vector a, Vector b){
	return a.x * b.x + a.y * b.y;
}

double cross(Vector a, Vector b){
	return a.x * b.y - a.y * b.x;
}

bool isOrthogonal(Vector a, Vector b){
	return equals(dot(a, b), 0);
}

bool isOrthogonal(Point a1, Point a2, Point b1, Point b2){
	return isOrthogonal(a1 - a2, b1 - b2);
}

bool isParallel(Vector a, Vector b){
	return equals(cross(a, b), 0);
}

bool isParallel(Point a1, Point a2, Point b1, Point b2){
	return isParallel(a1 - a2, b1 - b2);
}

Point projection(Segment s, Point p){
	Vector v1 = s.p2 - s.p1;
	Vector v2 = p - s.p1;
	return s.p1 + v1 * dot(v1, v2) / v1.norm();
}

Point reflection(Segment s, Point p){
	return p + (projection(s, p) - p) * 2.0;
}

double dist_p_p(Point p1, Point p2){
	return Vector(p1 - p2).abs();
}

double dist_p_l(Line l, Point p){
	Vector v1 = p - l.p1;
	Vector v2 = l.p2 - l.p1;
	return cross(v1, v2) / v2.abs();
}

double dist_p_s(Segment s, Point p){
	if (dot(p - s.p1, s.p2 - s.p1) < EPS) return (p - s.p1).abs();
	if (dot(p - s.p2, s.p1 - s.p2) < EPS) return (p - s.p2).abs();
	return dist_p_l(s, p);
}

