struct Point{ double x, y; };

Point set_p(double a, double b)
{
    Point p;
    p.x = a; p.y = b;
    return p;
}

struct Vector{ double vx, vy; };

Vector set_v(const Point &p1, const Point &p2)
{
    Vector v;
    v.vx = p2.x - p1.x;
    v.vy = p2.y - p1.y;
    return v;
}

double dot(const Vector &v1, const Vector &v2)
{
    return (v1.vx * v2.vx) + (v1.vy * v2.vy);
}

double cross(const Vector &v1, const Vector &v2)
{
	return (v1.vx * v2.vy) - (v1.vy * v2.vx);
}

double onenorm(const Vector &v)
{
	return abs(v.vx) + abs(v.vy);
}

int ccw(const Point &P0, const Point &P1, const Point &P2)
{
	Vector V1 = set_v(P0, P1);
	Vector V2 = set_v(P0, P2);
	double c = cross(V1, V2);
	if(c > 0){ return 1; }else if(c < 0){ return -1; }
	if(dot(V1, V2) < 0){ return -2; }
	if(onenorm(V1) < onenorm(V2)){ return 2; }
	return 0;
}

