#include<bits/stdc++.h>

using namespace std;

typedef complex<double> P;

string ccw(P a,P b,P c)
{
    P ab=b-a,ac=c-a;
    P tmp=ab*conj(ac);
    if(tmp.imag()>0)
    {
        return "CLOCKWISE";
    }else if(tmp.imag()<0)
    {
        return "COUNTER_CLOCKWISE";
    }else if(tmp.real()<0)
    {
        return "ONLINE_BACK";
    }else if(abs(ac)>abs(ab))
    {
        return "ONLINE_FRONT";
    }else
    {
        return "ON_SEGMENT";
    }
}

