#include <iostream>
#include <cmath>
using namespace std;

const double eps = 1e-9;
inline bool equals(double a, double b) { return fabs(a - b) < eps ;}

class Point {
public:
    double x, y;

    Point(double x = 0, double y = 0): x(x), y(y) {};

    Point operator + (Point p) { return Point(x + p.x, y + p.y); }
    Point operator - (Point p) { return Point(x - p.x, y - p.y); }
    Point operator * (double a) { return Point(a * x, a * y); }
    Point operator / (double a) { return Point(x / a, y / a); }

    double abs() { return sqrt(norm()); }
    double norm() { return x * x + y * y; }

    bool operator < (const Point &p) const {
        return x != p.x ? x < p.x : y < p.y;
    }

    bool operator == (const Point &p) const {
        return fabs(x - p.x) < eps && fabs(y - p.y) < eps;
    }
};
typedef Point Vector;

double dot(const Vector& a, const Vector& b) {
    return a.x * b.x + a.y * b.y;
}

// return abs of closs product
double cross(const Vector& a, const Vector& b) {
    return a.x * b.y - a.y * b.x;
}

int ccw(Point p0, Point p1, Point p2) {
    const int COUNTER_CLOCKWISE = 1, CLOCKWISE = -1;
    const int ONLINE_BACK = 2, ONLINE_FRONT = -2, ON_SEGMENT = 0;
    Vector a = p1 - p0;
    Vector b = p2 - p0;
    if (cross(a, b) >  eps) return COUNTER_CLOCKWISE;
    if (cross(a, b) < -eps) return CLOCKWISE;
    if (dot(a, b)   < -eps) return ONLINE_BACK;
    if (a.norm() < b.norm()) return ONLINE_FRONT;
    return ON_SEGMENT;
}

int main(void) {
    double x0, y0, x1, y1;
    cin >> x0 >> y0 >> x1 >> y1;
    Point a(x0, y0), b(x1, y1);
    int q;
    cin >> q;
    while (q--) {
        double x, y;
        cin >> x >> y;
        int judge = ccw(a, b, Point(x, y));
        if (judge == -2) cout << "ONLINE_FRONT";
        if (judge == -1) cout << "CLOCKWISE";
        if (judge == 0) cout << "ON_SEGMENT";
        if (judge == 1) cout << "COUNTER_CLOCKWISE";
        if (judge == 2) cout << "ONLINE_BACK";
        cout << endl;
    }
    return 0;
}

#include <cstdio>
