#define EPS 1e-10
using namespace std;

struct P{
	double x,y;
	P(double xx=0,double yy=0){
		x=xx;
		y=yy;
	}
	P operator +(P &p){
		return P(x+p.x,y+p.y);	
	}
	P operator -(P &p){
		return P(x-p.x,y-p.y);	
	}
	P operator *(double d){
		return P(x*d,y*d);	
	}
	P operator /(double d){
		return P(x/d,y/d);	
	}
	double abs(){
		return sqrt(norm());
	}
	double norm(){
		return x*x+y*y;
	}
	bool operator ==(const P &p)const{
		return fabs(x-p.x)<EPS && fabs(y-p.y)<EPS;
	}
	double dot(P p){
		return x*p.x+y*p.y;
	}
	double cross(P p){
		return x*p.y-y*p.x;
	}
};

typedef P Vector;

int ccw(P p0,P p1,P p2){
	Vector a=p1-p0;
	Vector b=p2-p0;
	if(a.cross(b)>EPS)return 1;
	if(a.cross(b)<-EPS)return -1;
	if(a.dot(b)<-EPS)return 2;
	if(a.norm()<b.norm())return -2;
	return 0;
}

int main(void){
	P p0,p1,p2;
	scanf("%lf %lf %lf %lf",&p0.x,&p0.y,&p1.x,&p1.y);
	int q;
	scanf("%d",&q);
	for(int i=0;i<q;i++){
		scanf("%lf %lf",&p2.x,&p2.y);
		int res=ccw(p0,p1,p2);
		if(res==2)printf("ONLINE_FRONT\n");
		if(res==1)printf("COUNTER_CLOCKWISE\n");
		if(res==0)printf("ON_SEGMENT\n");
		if(res==-1)printf("CLOCKWISE\n");
		if(res==-2)printf("ONLINE_BACK\n");
	}
	return 0;
}

