const double EPS = 1e-6;
typedef complex<double> comp;

void solve(void){

	int x1, y1, x2, y2, Q;
	cin >> x1 >> y1 >> x2 >> y2 >> Q;

	comp o(x1, y1), p(x2-x1, y2-y1), r;

	//回転用
	r = polar(1.0, arg(p));

	while(Q--)
	{
		int x3, y3;
		cin >> x3 >> y3;

		comp q(x3-x1, y3-y1);

		q /= r;

		printf("%.12lf %.12lf\n", norm(p), norm(q));

		if (abs(arg(q)) < EPS)
		{
			if (norm(p) >= norm(q) - EPS)
			{
				putln("ON_SEGMENT");
			}
			else
			{
				putln("ONLINE_FRONT");
			}
		}
		else if (abs(abs(arg(q))-M_PI) < EPS)
		{
			putln("ONLINE_BACK");
		}
		else if (arg(q) > 0)
		{
			putln("COUNTER_CLOCKWISE");
		}
		else
		{
			putln("CLOCKWISE");
		}
	}
}

int main(void){
	solve();
	//cout << "yui(*-v・)yui" << endl;
	return 0;
}

#include <cmath>
