using namespace std;

const double kEps = 1e-10;

int dcmp(double x) {
  if (fabs(x) < kEps) return 0;
  return x < 0 ? -1 : 1;
}

struct Vector {
  double x, y;
  Vector() {}
  Vector(double x, double y): x(x), y(y) {}
  // Vector(const Point & p1, const Point & p2): x(p2.x - p1.x), y(p2.y - p1.y) {}
  Vector(const Vector & v);

  double Norm() const { return hypot(x, y); }
  double NormSquared() const { return x * x + y * y; }
  Vector Normalize() const {
    return *this / this->Norm();
  }
  Vector operator-() const {
    return Vector(-x, -y);
  }
  Vector operator+(const Vector & rhs) const {
    return Vector(x + rhs.x, y + rhs.y);
  }
  Vector operator-(const Vector & rhs) const {
    return *this + (-rhs);
  }
  Vector operator*(const double rhs) const {
    return Vector(rhs * x, rhs * y);
  }
  Vector operator/(const double rhs) const {
    return *this * (1.0 / rhs);
  }
  double Dot(const Vector & rhs) const {
    return x * rhs.x + y * rhs.y;
  }
  double Cross(const Vector & rhs) const {
    return x * rhs.y - y * rhs.x;
  }
  bool operator==(const Vector & rhs) const {
    return dcmp(x - rhs.x) == 0 && dcmp(y - rhs.y) == 0;
  }
  bool operator<(const Vector & rhs) const {
    return dcmp(x - rhs.x) < 0 || (dcmp(x - rhs.x) == 0 && dcmp(y - rhs.y) < 0);
  }
};

struct Point {
  Point() {}
  Point(double x, double y): x(x), y(y) {}
  Point(const Point & p);

  double x, y;

  Point operator-() {
    return Point(-x, -y);
  }
  Point operator+(const Vector & rhs) const {
    return Point(x + rhs.x, y + rhs.y);
  }
  Point operator-(const Vector & rhs) const {
    return *this + (-rhs);
  }
  bool operator==(const Point & rhs) const {
    return dcmp(x - rhs.x) == 0 && dcmp(y - rhs.y) == 0;
  }
  bool operator<(const Point & rhs) const {
    return dcmp(x - rhs.x) < 0 || (dcmp(x - rhs.x) == 0 && dcmp(y - rhs.y) < 0);
  }
};

struct Line {
  Line() {}
  Line(const Point & p1, const Point & p2): p1(p1), p2(p2) {}
  Line(double x1, double y1, double x2, double y2): p1(x1, y1), p2(x2, y2) {}
  Line(const Line & l);

  Point p1, p2;
};

ostream & operator<<(ostream & os, const Point & p) {
  os << p.x << " " << p.y;
  return os;
}

ostream & operator<<(ostream & os, const Vector & v) {
  os << v.x << " " << v.y;
  return os;
}

// minus: left, plus: right, 0: colinear
int Direction(const Point & p0, const Point & p1, const Point & p2) {
  Vector v1(p1.x - p0.x, p1.y - p0.y);
  Vector v2(p2.x - p0.x, p2.y - p0.y);
  return v2.Cross(v1);
}

// minus: back, plus: front, 0: on
int SegmentDirection(const Point & p0, const Point & p1, const Point & p2) {
  if (p2.x >= min(p0.x, p1.x) && p2.x <= max(p0.x, p1.x)
      && p2.y >= min(p0.y, p1.y) && p2.y <= max(p0.y, p1.y)) {
    return 0;
  } else if (p2.x >= max(p0.x, p1.x)) {
    return 1;
  } else {
    return -1;
  }
}

int main(int argc, char const *argv[]) {
  double x0, y0, x1, y1;
  cin >> x0 >> y0 >> x1 >> y1;
  Point p0(x0, y0);
  Point p1(x1, y1);

  int q;
  cin >> q;
  for (unsigned i = 0; i < q; ++i) {
    double x2, y2;
    cin >> x2 >> y2;
    Point p2(x2, y2);
    auto direction = Direction(p0, p1, p2);
    auto s_direction = SegmentDirection(p0, p1, p2);

    if (direction > 0) {
      cout << "CLOCKWISE\n";
    } else if (direction < 0) {
      cout << "COUNTER_CLOCKWISE\n";
    } else if (s_direction > 0) {
      cout << "ONLINE_FRONT\n";
    } else if (s_direction < 0) {
      cout << "ONLINE_BACK\n";
    } else {
      cout << "ON_SEGMENT\n";
    }
  }
  return 0;
}

