/*
v1 = p1 - p0, v2 = p2 - p0 とします。
det(v1,v2) が正なら半時計、負なら時計回りです。
0 なら、内積 v1*v2 をチェックです。
それが負ならバック、0 以上 v1*v1 以下ならオン、それ以外はフロントです。
*/

int main() {
  std::cin.tie(0); std::cin.sync_with_stdio(false);
  int x0, y0, x1, y1;
  std::cin >> x0 >> y0 >> x1 >> y1;
  auto v1 = std::make_pair(x1-x0, y1-y0);

  auto det = [&] (auto&& p, auto&& q) {
    int a,b,c,d;
    std::tie(a,b) = p;
    std::tie(c,d) = q;
    return a*d - b*c;
  };

  auto inp = [&] (auto&& p, auto&& q) {
    int a,b,c,d;
    std::tie(a,b) = p;
    std::tie(c,d) = q;
    return a*c + b*d;
  };

  int L = inp(v1,v1);

  int q; std::cin >> q;
  loop(q) {
    int x2, y2; std::cin >> x2 >> y2;
    std::pair<int,int> v2{ x2-x0, y2-y0 };
