      std::cout << "COUNTER_CLOCKWISE" << std::endl;
    } else if (d < 0) {
      std::cout << "CLOCKWISE" << std::endl;
    } else {
      int i = inp(v1,v2);
      if (i < 0) {
        std::cout << "ONLINE_BACK" << std::endl;
      } else if (L < i) {
        std::cout << "ONLINE_FRONT" << std::endl;
      } else {
        std::cout << "ON_SEGMENT" << std::endl;
      }
    }
  }
  return 0;
}

