string ccw(point a,point b,point c) {
  vectol ab=b-a, ac =c-a;
  vectol d =ac*conj(ab); 
  if(d.y > 0) return "COUNTER_CLOCKWISE";
  if(d.y < 0) return "CLOCKWISE";
  ab = ab*conj(ab);
  if(ab.x<d.x) return "ONLINE_FRONT";
  if(0>d.x) return "ONLINE_BACK";
  return "ON_SEGMENT";
}

