D inp(P a, P b) {
    return a.x*b.x + a.y*b.y;
}

D outp(P a, P b) {
    return a.x*b.y - a.y*b.x;
}

D norm(P p) {
    return inp(p, p);
}

D abs(P p) {
    return sqrt(norm(p));
}

