double distanceLinePoint(Segment s, Point p){
	// sin?? = |cross(hypo, base)|/|hypo||base|
	// distance = |hypo|sin?? = |cross(hypo, base)|/|base|
	Vector hypo = p - s.p1;
	Vector base = s.p2 - s.p1;
	return abs(cross(hypo, base)) / base.abs();
}

// ?????¨??????????????¢
double distanceSegmentPoint(Segment s, Point p){
	// (p2-p1)??¨???p-p1??????90???????????§????????´??????p??¨p1????????¢
	// (p1-p2)??¨???p-p2??????90???????????§????????´??????p??¨p2????????¢
	// ????????\????????´???????????¨??´???????????¢
	if(dot(s.p2-s.p1, p-s.p1) < 0.0) return (p - s.p1).abs();
	if(dot(s.p1-s.p2, p-s.p2) < 0.0) return (p - s.p2).abs();
	return distanceLinePoint(s, p);
}

// ????????¨??????????????¢
double distanceSegmentSegment(Segment s1, Segment s2){
	if(intersect(s1, s2)) return 0.0;
	double ret = distanceSegmentPoint(s1, s2.p1);
	ret = min(ret, distanceSegmentPoint(s1, s2.p2));
	ret = min(ret, distanceSegmentPoint(s2, s1.p1));
	ret = min(ret, distanceSegmentPoint(s2, s1.p2));
	return ret;
}


