class Line{
    public:
    std::complex<double> basepoint;
    std::complex<double> direct;
    Line(){}
    Line(const std::complex<double> &_basepoint, const std::complex<double> &_direct):basepoint(_basepoint){
        assert(abs(_direct) != 0);
        direct = _direct/abs(_direct);
    }
    std::complex<double> basesTrans(const std::complex<double> &p) const { return (p-basepoint)/direct;}
    bool parallel(const std::complex<double> l) const { return basesTrans(l).imag() == 0;}
    bool online(const std::complex<double> &p) const { return parallel(p - basepoint);}
};

class LineSeg: public Line{ //Line Segment
    public:
    std::complex<double> endpoint;
    LineSeg(){}
    LineSeg(const std::complex<double> &_basepoint, const std::complex<double> &_endpoint)
    :Line(_basepoint, _endpoint - _basepoint), endpoint(_endpoint){
        direct = endpoint - basepoint;
    }
    LineSeg(const Line &l, std::complex<double> &p):Line(l) {
        assert(l.online(p));
        endpoint = p;
        direct = endpoint - basepoint;
    }
};

