string signed_area(int xp2, int yp2, Point p0, Point p1){
    double area2;
    area2 = (p0.x - xp2) * (p1.y - yp2) - (p0.y - yp2) * (p1.x - xp2);
    Point vec1, vec2;
    vec1.x = (p1.x-p0.x);
    vec1.y = (p1.y-p0.y);
    vec2.x = (xp2-p0.x);
    vec2.y = (yp2-p0.y);
    double naiseki;
    naiseki = vec1.x * vec2.x + vec1.y * vec2.y;
    //cout << "areea2:" << area2 << endl;
    //cout << naiseki << endl;
    if(area2 > 0){
        return "COUNTER_CLOCKWISE";
    }
    else if(area2 < 0){
        return "CLOCKWISE";
    }
    else {
        if(naiseki < 0){
            return "ONLINE_BACK";
        }
        else if(naiseki > 0){
            double leng1, leng2;
            leng1 = (p1.x-p0.x)*(p1.x-p0.x)+(p1.y-p0.y)*(p1.y-p0.y);
            leng2 = (xp2-p0.x)*(xp2-p0.x)+(yp2-p0.y)*(yp2-p0.y);
            if(leng1 < leng2){
                return "ONLINE_FRONT";
            }
            else {
                return "ON_SEGMENT";
            }
        }
        else {
            return "ON_SEGMENT";
        }
    }
}


