    db dis(P p) { return sqrt(sqr(x-p.x) + sqr(y-p.y)); }
    db dis2(P p) { return sqr(dis(p)); }
};

P getProjection(P a, P b, P p) {
    // (p1, q) 在 (p1, q2) 上的投影点
    P dir = b-a;
    return a + dir * ( dir.dot(p-a) / a.dis2(b) );
}

P getReflection(P a, P b, P p) {
    return getProjection(a, b, p) * 2 - p;
}

