	return proj(a,b)*2-b;
}
int dblcmp(db x) {
	return fabs(x)<eps?0:(x>0?1:-1);
}
int relation(const line &a,const point &b) {
	int x=dblcmp(det(a.b-a.a,b-a.a));
	if (x==1) return 0; // COUNTER_CLOCKWISE
	else if (x==-1) return 1; // CLOCKWISE
	else {
		x=dblcmp(dot(b-a.a,a.b-a.a));
		if (x<-eps) return 2; // ONLINE_BACK
		else {
			x=dblcmp(dot(b-a.b,a.b-a.a));
			if (x>eps) return 3; // ONLINE_FRONT
			else return 4; // ON_SEGMENT
		}
	}
}

