int cross(Point p1, Point p2){ return p1.x*p2.y-p1.y*p2.x; }
ll lengthSQ(Point vec){ return SQ(ll(vec.x))+SQ(ll(vec.y)); }
void solve(Point pt2){
	const Point vec1 = vector(pt0, pt1);
	const ll vec1Len2 = lengthSQ(vec1);
	Point vec2 = vector(pt0, pt2);
	int cro = cross(vec1, vec2);
	if(cro>0) printf("COUNTER_CLOCKWISE\n");// a×b>0，则b在a的逆时针方向
	else if(cro<0) printf("CLOCKWISE\n");// a×b<0，则b在a的顺时针方向
	else if(dot(vec1, vec2)<0) printf("ONLINE_BACK\n");// a×b=0, a·b<0，则a、b方向相反
	else if(lengthSQ(vec2)>vec1Len2) printf("ONLINE_FRONT\n");
	else printf("ON_SEGMENT\n");
}
