int ccwise(Point a, Point b, Point c) {
    b -= a; c -= a;

    if (cross_prod(b, c) > 0) {
        // conter clockwise
        return 1;
    } else if (cross_prod(b, c) < 0) {
        // clockwise
        return -1;
    } else if (dot_prod(b, c) < 0) {
        // online back
        return 2;
    } else if (norm(b) < norm(c)) {
        // online front
        return -2;
    } else {
        // on segment
        return 0;
    }
}

