#include <bits/stdc++.h>
using namespace std;

struct Point
{
	int x;
	int y;
};

bool inPoint(Point a, Point b, Point c)
{
	if(min(a.x,c.x)<=b.x && max(a.x,c.x)>=b.x && min(a.y,c.y)<=b.y && max(a.y,c.y)>=b.y) return true;
	return false;
}

int orientation(Point a, Point b, Point c)
{
	int val = (b.x-a.x)*(c.y-a.y)-(b.y-a.y)*(c.x-a.x);
	//clockwise
	if(val<0) return -1;
	//counter
	else if(val>0) return 1;
	//collinear
	else return 0;
}

void turn(Point a, Point b, Point c)
{
	int val = orientation(a,b,c);
	if(val==-1) cout<<"CLOCKWISE"<<endl;
	else if(val==1) cout<<"COUNTER_CLOCKWISE"<<endl;
	else if(inPoint(a, c, b)) cout<<"ON_SEGMENT"<<endl;
	else if(inPoint(c, a, b)) cout<<"ONLINE_BACK"<<endl;
	else cout<<"ONLINE_FRONT"<<endl;
}

