      printf("COUNTER_CLOCKWISE\n");
    else if(cro < 0)
      printf("CLOCKWISE\n");
    else {
      double norm10 = norm(p1, p0);
      double norm20 = norm(p2, p0);
      //printf("%lf %lf\n", norm10, norm20);
