template<typename T>
class n32 {
  const double EPS = 0.000001;
public:
  int operator () (std::complex<T> L1, std::complex<T> L2, std::complex<T> P) {
    if( ((P - L1) * conj(L2 - L1)).imag() > EPS ) return 1;
    else if( ((P - L1) * conj(L2 - L1)).imag() < -EPS ) return -1;
    return 0;
  }
};

