int ccw(const Point& p1, const Point& p2, const Point& p) {
    double cr = Point::cross(p2 - p1, p - p1);
    if (cr > 0) return 1;
    if (cr < 0) return 2;
    if (Point::dot(p2 - p1, p - p1) < 0) { return 3; }
    if (Point::dot(p - p2, p1 - p2) < 0) { return 4; }
    return 5;
}

