struct Vector {
    double x = 0, y = 0;
    Vector(int _x, int _y) : x(_x), y(_y) {}
    Vector(double _x, double _y) : x(_x), y(_y) {}

    double dot(const Vector &r) const {
        return x * r.x + y * r.y;
    }

    double cross(const Vector &r) const {
        return x * r.y - y * r.x;
    }

    double length() const {
        return sqrt(x * x + y * y);
    }

    Vector  operator +  (const Vector &r) const {
        return Vector(x + r.x, y + r.y);
    }
    Vector& operator += (const Vector &r) {
        x += r.x, y += r.y;
        return *this;
    }

    Vector  operator -  () const {
        return Vector(-x, -y);
    }

    Vector  operator -  (const Vector &r) const {
        return *this + -r;
    }
    Vector& operator -= (const Vector &r) {
        x -= r.x, y -= r.y;
        return *this;
    }

    Vector  operator *  (double k) const {
        Vector tmp = *this;
        tmp.x *= k, tmp.y *= k;
        return tmp;
    }

    Vector& operator *= (double k) {
        x *= k, y *= k;
        return *this;
    }

    // 向量单位化
    void unitise() {
        double len = length();
        x /= len, y /= len;
    }
};
typedef Vector Point;
struct Line {
    Point p;
    // 规定方向向量取单位向量
    Vector dir;

    Line(Point _p, Vector _dir) : p(_p), dir(_dir) {
        dir.unitise();
    }

};

// 向量数乘
Vector operator * (double k, const Vector & r) {
    return r * k;
}

// 点到直线的投影点
Point projection(const Point &p, const Line &l) {
    double d = Vector(p - l.p).dot(l.dir);
    return l.p + d * l.dir;
}
// 点关于直线的对称点
Point reflection(const Point &p, const Line &l) {
    Point pro = projection(p, l);
    return p + 2 * (pro - p);
}
