    for(int i = 0; i < q; i++){
        Pint p2;
        cin >> p2.first >> p2.second;

        if(vector_equal(p2,p1)||vector_equal(p2,p0)){
            cout << "ON_SEGMENT" << endl;
            continue;
        }

        clock_wise(p0,p1,p2);
    }

    return 0;
}

