struct Point{double x,y;}p0,p1,p2;
Point operator -(Point a,Point b){return (Point){a.x-b.x,a.y-b.y};}
double cross(Point a,Point b){return a.x*b.y-a.y*b.x;}
double dot(Point a,Point b){return a.x*b.x+a.y*b.y;}
double norm(Point a){return a.x*a.x+a.y*a.y;}
void read(Point &p){p.x=read(),p.y=read();}
int Clockwise(Point p0,Point p1,Point p2)
{
	Point v1=p1-p0,v2=p2-p0;
	if(cross(v1,v2)>0)return -2;
	if(cross(v1,v2)<0)return 2;
	if(dot(v1,v2)<0)return -1;
	if(norm(v1)<norm(v2))return 1;
	return 0;
}
int main()
{
	read(p0),read(p1);
	int q=read();while(q--)
	{
		read(p2);
		switch(Clockwise(p0,p1,p2))
		{
			case -2:printf("COUNTER_CLOCKWISE\n");break;
			case -1:printf("ONLINE_BACK\n");break;
			case 0:printf("ON_SEGMENT\n");break;
			case 1:printf("ONLINE_FRONT\n");break;
			case 2:printf("CLOCKWISE\n");break;
		}
	}
	return 0;
}

