		cin >> p.x >> p.y;
		switch (ccw(p0, p1, p)) {
		case COUNTER_CLOCKWISE:
			cout << "COUNTER_CLOCKWISE" << endl;
			break;
		case CLOCKWISE:
			cout << "CLOCKWISE" << endl;
