  temp=p1;
  p1*=conj(p1);
  p2*=conj(p1);
  if(abs(temp)==0) return "ON_SEGMENT";
  p1/=abs(temp);
  if(p2.imag()>0) return "COUNTER_CLOCKWISE";
  if(p2.imag()<0) return "CLOCKWISE";
  p2/=abs(temp);
  if(p2.real()<0) return "ONLINE_BACK";
  if(p1.real()<p2.real()) return "ONLINE_FRONT";
  return "ON_SEGMENT";
}

