	Point p1(x1,y1),p2(x2,y2);
	int T;scanf("%d",&T);
	while(T--){
		int x,y;
		scanf("%d %d",&x,&y);
		Point p(x,y);
		if(ccw(p1,p2,p)==1) printf("COUNTER_CLOCKWISE\n");
		else if(ccw(p1,p2,p)==-1) printf("CLOCKWISE\n");
		else if(ccw(p1,p2,p)==2) printf("ONLINE_BACK\n");
		else if(ccw(p1,p2,p)==-2) printf("ONLINE_FRONT\n");
		else printf("ON_SEGMENT\n");
	} 

	return 0;
}

