bool onSegment(Line l, const Point& q) {
  return sign(cross(q - l[0], l[1] - l[0])) == 0 && 
    inMid(l[0].x, l[1].x, q.x) && inMid(l[0].y, l[1].y, q.y);
}
// q 在线段 AB 上
bool onSegment(Point a, Point b, Point q) {
  return onSegment(Line(a, b), q);
}

