        else if(abs(cos + 1) < 0.000001) cout << "ONLINE_BACK" << endl;
        else if(sin < 0) cout << "CLOCKWISE" << endl;
        else if(sin > 0) cout << "COUNTER_CLOCKWISE" << endl;
        else cout << "ON_SEGMENT" << endl;
    }
}

