// CGL_1_C
// p0->p1 and p0->p2, query is p2
int counter_clockwise(C p0, C p1, C p2) {
    C a = std::conj(p1-p0) * (p2-p0);
    if (a.imag() >  EPS) { return +1; } // counter clockwise
    if (a.imag() < -EPS) { return -1; } // clockwise
    if (a.real() < -EPS) { return +2; } // p2-p0-p1
    if (std::norm(p1-p0) < std::norm(p2-p0)) { return -2; } // p0-p1-p2
    return 0; // p0-p2-p1
}

void solve_CGL_1_C() {
    int x1, y1, x2, y2;
    std::cin >> x1 >> y1 >> x2 >> y2;
    C p0(x1,y1), p1(x2,y2);
    int q;
    std::cin >> q;
    rep(i,q) {
        int x, y;
        std::cin >> x >> y;
        C p2 = C(x,y);
        int res = counter_clockwise(p0, p1, p2);
        if (res == 1) {
            printf("COUNTER_CLOCKWISE\n");
        }
        if (res == -1) {
            printf("CLOCKWISE\n");
        }
        if (res == 2) {
            printf("ONLINE_BACK\n");
        }
        if (res == -2) {
            printf("ONLINE_FRONT\n");
        }
        if (res == 0) {
            printf("ON_SEGMENT\n");
        }
    }
}

