// Actual Solver
inline void solve()
{
    Line l;
    l.read();
    int q;
    cin >> q;
    while (q--)
    {
        Point p;
        p.read();
        int re = l.relation(p);
        if (re == 1)
            cout << "COUNTER_CLOCKWISE\n";
        else if (re == 2)
            cout << "CLOCKWISE\n";
        else
        {
            auto d = p.dis(l.s);
            auto a = l.s.rad(l.e, p);
            if (sgn(a - PI) == 0)
                cout << "ONLINE_BACK\n";
            else if (sgn(d - l.len()) < 0)
                cout << "ON_SEGMENT\n";
            else
                cout << "ONLINE_FRONT\n";
        }
    }
}

