int main()
{
	cin.tie(0);
	ios::sync_with_stdio(false);

	double x1, x2, y1, y2, x3, y3;
	cin >> x1 >> y1 >> x2 >> y2;

	x3 = (x2 - x1) / sqrt((x2 - x1)*(x2 - x1) + (y2 - y1)*(y2 - y1));
	y3 = (y2 - y1) / sqrt((x2 - x1)*(x2 - x1) + (y2 - y1)*(y2 - y1));

	int q;
	cin >> q;

	for (int i = 0; i < q; i++)
	{
		double px, py, px2, py2;
		cin >> px >> py;

		px2 = (px - x1);
		py2 = (py - y1);

		if ((px2*px2 + py2*py2) <= 0.0000001)
		{
			cout << "ON_SEGMENT" << endl;
			continue;
		}

		double cross = (py2*x3 - px2*y3);
		if (abs(cross) <= 0.00000000001)
		{
			if ((px2*x3 + py2*y3)>0)
			{
				if ((x3*x3 + y3*y3) < (px2*px2 + py2*py2))
				{
					cout << "ONLINE_FRONT" << endl;
				}
				else
				{
					cout << "ON_SEGMENT" << endl;
				}
			}
			else
			{
				cout << "ONLINE_BACK" << endl;
			}
		}
		else
		{
			if (cross > 0)
			{
				cout << "COUNTER_CLOCKWISE" << endl;
			}
			else
			{
				cout << "CLOCKWISE" << endl;
			}
		}
	}

	return 0;
}

