#define EPS 1e-8
#define EQ(a, b) (abs((a)-(b)) < EPS)

inline int signum(double x) { return (abs(x) < EPS ? 0 : x > 0 ? 1 : -1); }

typedef complex<double> P;

double cross(P a, P b) {
    return a.real() * b.imag() - a.imag() * b.real();
}

double dot(P a, P b) {
    return a.real() * b.real() + a.imag() * b.imag();
}

int ccw(P p0, P p1, P p2) {
    p1 -= p0;
    p2 -= p0;
    int sign = signum(cross(p1, p2));
    if (sign != 0) return sign;
    if (dot(p1, p2) < -EPS) return 2; //left
    if (abs(p1) < abs(p2)) return -2; //right
    return 0; //mid
}

bool is_parallel(P p0, P p1, P p2, P p3) {
    P x = p1 - p0;
    P y = p3 - p2;
    return EQ(cross(x, y), 0.0);
}

bool is_orthogonal(P p0, P p1, P p2, P p3) {
    P x = p1 - p0;
    P y = p3 - p2;
    return EQ(dot(x, y), 0.0);
}

