class Point {
public:
    double _x, _y;
    Point(): _x(0), _y(0){}
    Point(double x, double y): _x(x), _y(y){}
    Point(const Point& a): _x(a._x), _y(a._y){}

    Point operator+(const Point& a) const{
        return Point(_x + a._x, _y + a._y);
    }
    Point operator-(const Point& a) const{
        return Point(_x - a._x, _y - a._y);
    }
    Point operator*(double& a) const{
        return Point(_x*a, _y*a);
    }
    Point operator/(double& a) const{
        return Point(_x/a, _y/a);
    }
    void operator=(const Point& a){
        _x = a._x;
        _y = a._y;
    }
    double dot(const Point& a) const{
        return (_x*a._x + _y*a._y);
    }
    double norm() const{
        return sqrt(_x*_x + _y*_y);
    }
};

Point operator*(double a, const Point& vec){
    return vec*a;
}
Point operator/(double a, const Point& vec){
    return vec/a;
}

double cross(const Point& p0, const Point& p1){
    return p0._x * p1._y - p0._y*p1._x;
}

int ccw(Point a, Point b){
    double ret=cross(a,b);
    if(ret > MINV){
        return 1;
    }else if( (-ret) > MINV ){
        return 2;
    }
    if(a.dot(b) < 0) return 3;
    if(a.norm() < b.norm()){
        return 4;
    }else{
        return 5;
    }

}

