		int ax = x1 - x0;
		int ay = y1 - y0;
		int bx = x2 - x0;
		int by = y2 - y0;

		double ad = dist(ax, ay);
		double bd = dist(bx, by);

		double at = angle(ax, ay);
		double bt = angle(bx, by);

		double st = bt - at;

		if (st < -PI - EPS)
		{
			st += 2 * PI;
		}

		if (st > PI + EPS)
		{
			st -= 2 * PI;
		}

		if (bx == 0 && by == 0)
		{
			cout << "ON_SEGMENT" << endl;
		}
		else if (PI - EPS < abs(st) && abs(st) < PI + EPS)
		{
			cout << "ONLINE_BACK" << endl;
		}
		else if (-EPS < st && st < EPS)
		{
			if (ad < bd)
			{
				cout << "ONLINE_FRONT" << endl;
			}
			else
			{
				cout << "ON_SEGMENT" << endl;
			}
		}
		else if (st > EPS)
		{
			cout << "COUNTER_CLOCKWISE" << endl;
		}
