using namespace std;

namespace geometry {
	using Real = double;
	using Point = complex<Real>;
	constexpr Real EPS = 1e-9;
	struct Circle {
		Point p;
		Real r;
		Circle(const Point p, const Real r) : p(p), r(r) {}
	};
	struct Line {
		Point a, b;
		Line(const Point a, const Point b) : a(a), b(b) {}
	};
	// 内積
	inline Real dot(const Point& lhs, const Point& rhs) {
		return (conj(lhs) * rhs).real();
	}
	// 外積
	inline Real cross(const Point& lhs, const Point& rhs) {
		return (conj(lhs) * rhs).imag();
	}
	// 円の交点
	vector<Point> intersect(const Circle& c1, const Circle& c2) {
		const Real dist = abs(c1.p - c2.p);
		vector<Point> ret;
		if(dist > c1.r + c2.r) return ret;
		if(dist < abs(c1.r - c2.r)) return ret;
		const Real rc = (dist * dist + c1.r * c1.r - c2.r * c2.r) / (2 * dist);
		const Real rs = sqrt(c1.r * c1.r - rc * rc);
		const Point vec = (c2.p - c1.p) / dist;
		ret.emplace_back(c1.p + vec * Point(rc, rs));
		ret.emplace_back(c1.p + vec * Point(rc, -rs));
		return ret;
	}
	// a の b に対する正射影ベクトル
	inline Point projection(const Point& a, const Point& b) {
		return b * dot(a, b) / norm(b);
	}
	// 点pから直線lに下ろした垂線の足
	inline Point projection(const Line& l, const Point& p) {
		return projection(p - l.a, l.b - l.a) + l.a;
	}
	// 直線lを対称軸として点pと線対称な点
	inline Point reflection(const Line &l, const Point &p) {
		return p + (projection(l, p) - p) * 2.0;
	}
	// 反時計回り(a -> b -> c)
	static constexpr int COUNTER_CLOCKWISE = 0b00001;
	// 時計回り(a -> b -> c)
	static constexpr int CLOCKWISE = 0b00010;
	// 同一直線上(c -> a -> b)
	static constexpr int ONLINE_BACK = 0b00100;
	// 同一直線上(a -> b -> c)
	static constexpr int ONLINE_FRONT = 0b01000;
	// 同一直線上(a -> c -> b)
	static constexpr int ON_SEGMENT = 0b10000;
	// 3点の位置関係
	int ccw(const Point &a, Point b, Point c) {
		b = b - a, c = c - a;
		if(cross(b, c) > EPS) return COUNTER_CLOCKWISE;
		if(cross(b, c) < -EPS) return CLOCKWISE;
		if(dot(b, c) < 0) return ONLINE_BACK;
		if(norm(b) < norm(c)) return ONLINE_FRONT;
		return ON_SEGMENT;
	}


	// 直線の交点
	// Point intersect(const Line& l1, const Line& l2) {
	// 	const Point v1 = l1.p2 - l1.p1;
	// 	const Point v2 = l2.p2 - l2.p1;

	// }

	inline istream& operator>>(istream& is, Point& p) {
		Real x, y;
		is >> x >> y;
		p = Point(x, y);
		return is;
	}
	inline ostream& operator<<(ostream& os, const Point &p) {
		return os << fixed << setprecision(15) << p.real() << " " << p.imag();
	}
}

using namespace geometry;
int main() {
	Point p0, p1; cin >> p0 >> p1;
	int q; cin >> q;
	while(q--) {
		Point p2; cin >> p2;
		int ret = ccw(p0, p1, p2);
		string ans;
		if(ret & COUNTER_CLOCKWISE) ans = "COUNTER_CLOCKWISE";
		if(ret & CLOCKWISE) ans = "CLOCKWISE";
		if(ret & ONLINE_BACK) ans = "ONLINE_BACK";
		if(ret & ONLINE_FRONT) ans = "ONLINE_FRONT";
		if(ret & ON_SEGMENT) ans = "ON_SEGMENT";
		cout << ans << '\n';
	}
	return 0;
}

