            if(X > 0) {
                cout << "COUNTER_CLOCKWISE" << '\n';
            } else {
                cout << "CLOCKWISE" << '\n';
            }
        } else {
            if((p2-p1).dot(Q-p1) < 0) {
                cout << "ONLINE_BACK" << '\n';
            } else if(sqdist(Q, p1) > sqdist(p2, p1)) {
                cout << "ONLINE_FRONT" << '\n';
            } else {
                cout << "ON_SEGMENT" << '\n';
            }
        }
    }
}

