double cross(const Point &a, const Point &b) {
  return a.real() * b.imag() - a.imag() * b.real();
}

double dot(const Point& a, const Point &b) {
  return a.real() * b.real() + a.imag() * b.imag();
}

//https://mathtrain.jp/projection
Point projection(const Line &l, const Point &p){
  double t = dot(p - l.a, l.a-l.b) / norm(l.a - l.b);
  return l.a + (l.a - l.b) * t;
}

Point projection(const Segment &l, const Point &p){
  double t = dot(p - l.a, l.b-l.a) / norm(l.a - l.b);
  return l.a + (l.b - l.a) * t;
}

Point reflection(const Line &l, const Point &p){
  return p + (projection(l, p) - p) * 2.0;
}

int ccw(const Point &a, Point b, Point c) {
  b -= a, c -= a;
  if(cross(b, c) > EPS) return 1;   // "COUNTER_CLOCKWISE"
  if(cross(b, c) < -EPS) return -1; // "CLOCKWISE"
  if(dot(b, c) < -EPS) return 2;    // "ONLINE_BACK" c-a-b
  if(norm(b) < norm(c)) return -2;  // "ONLINE_FRONT" a-b-c
  return 0;                         // "ON_SEGMENT" a-c-b

}
int main(){
  Point a, b;
  cin >> a >> b;
  int q; cin >> q;
  while(q--){
    Point p; cin >> p;
    int t = ccw(a, b, p);
    string s;
    switch(t){
      case 2: s = "ONLINE_BACK"; break;
      case 1: s = "COUNTER_CLOCKWISE"; break;
      case 0: s = "ON_SEGMENT"; break;
      case -1: s = "CLOCKWISE"; break;
      case -2: s = "ONLINE_FRONT"; break;
      default: break;
    };
    cout << s << endl;
  }
}

