/*角abcの角度　a(xa,ya), b(xb,yb), c(xc,yc),*/
/*2辺の角度*/
/*aとb　またはbとcが重なっていればPIを返す*/
double angle_of_the_two_edges(double ax, double ay, double bx, double by, double cx, double cy){
    if(ax==bx&&ay==by)return M_PI;
    if(cx==bx&&cy==by)return M_PI;

    double angle1 = angle_of_the_line(ax, ay, bx, by);
    double angle2 = angle_of_the_line(bx, by, cx, cy);

    double ret = angle1+(M_PI-angle2);
    if(ret>M_PI * 2)ret -= M_PI * 2;
    if(ret<0)ret += M_PI * 2;
    return /*2*M_PI- */ret;

}

