/*二次元配列のクイックソート*/
/*xoryが0ならx座標　1なら座標でソート*/
void two_dimensional_qsort(double xy[100000][2], int left, int right, int xory)
{
    int i, j;
    double pivot,pivot2;

    i = left;                      /* ソートする配列の一番小さい要素の添字 */
    j = right;                     /* ソートする配列の一番大きい要素の添字 */

    pivot = xy[(left + right) / 2][xory]; /* 基準値を配列の中央付近にとる */
    pivot2 = xy[(left + right) / 2][(xory+1)%2];


    while (1) {                    /* 無限ループ */

        while (xy[i][xory] < pivot || (xy[i][xory] == pivot && xy[i][(xory+1)%2] < pivot2))       /* pivot より大きい値が */
            i++;                   /* 出るまで i を増加させる */

        while (pivot < xy[j][xory] || (xy[j][xory] == pivot &&  pivot2 < xy[j][(xory+1)%2]))       /* pivot より小さい値が */
            j--;                   /*  出るまで j を減少させる */
        if (i >= j)                /* i >= j なら */
            break;                 /* 無限ループから抜ける */

        swap(xy[i][xory], xy[j][xory]);            /* x[i] と x[j]を交換 */
        swap(xy[i][(xory+1)%2], xy[j][(xory+1)%2]);            /* x[i] と x[j]を交換 */
        i++;                       /* 次のデータ */
        j--;
    }

    if (left < i - 1)              /* 基準値の左に 2 以上要素があれば */
        two_dimensional_qsort(xy, left, i - 1, xory);     /* 左の配列を Q ソートする */
    if (j + 1 <  right)            /* 基準値の右に 2 以上要素があれば */
        two_dimensional_qsort(xy, j + 1, right, xory);    /* 右の配列を Q ソートする */
}


