    Point p1 = Point(x2, y2);
    scanf("%d", &q);

    for (int i = 0; i < q; i++) {
        scanf("%d %d", &px, &py);
        int ret = ccw(p0, p1, Point(px, py));

        switch (ret) {
            case -2: printf("ONLINE_FRONT\n"); break;
            case -1: printf("CLOCKWISE\n"); break;
            case 0:  printf("ON_SEGMENT\n"); break;
            case 1:  printf("COUNTER_CLOCKWISE\n"); break;
            case 2:  printf("ONLINE_BACK\n"); break;
        }
    }

    return 0;
}

