using namespace std;

struct Vec2;

double SqrMagnitude(const Vec2& v);

double Magnitude(const Vec2& v);

double Dot(const Vec2& v1, const Vec2& v2);


struct Vec2 {
    double x, y;
    Vec2() = default;
    Vec2(double in_x, double in_y)
        : x {in_x}, y {in_y} {};
    Vec2(const Vec2&) = default;
    Vec2(Vec2&&) = default;

    Vec2 normalized() const{
        double mag = Magnitude(*this);
        return {x / mag, y / mag};
    }

    Vec2 normalize() {
        double mag = Magnitude(*this);
        return {x /= mag, y /= mag};
    }

    Vec2 operator-() {
        return {-x, -y};
    }

    Vec2 operator+(const Vec2& rhs) const{
        return {x + rhs.x, y + rhs.y};
    }

    Vec2 operator-(const Vec2& rhs) const{
        return {x - rhs.x, y - rhs.y};
    }

    Vec2 operator*(double rhs) const{
        return {x * rhs, y * rhs};
    }

    Vec2 operator/(double rhs) const{
        return {x / rhs, y / rhs};
    }

    Vec2& operator+=(const Vec2& rhs) {
        x += rhs.x;
        y += rhs.y;
        return *this;
    }

    Vec2& operator-=(const Vec2& rhs) {
        x -= rhs.x;
        y -= rhs.y;
        return *this;
    }

    Vec2& operator*=(double rhs) {
        x *= rhs;
        y *= rhs;
        return *this;
    }

    Vec2& operator/=(double rhs) {
        x /= rhs;
        y /= rhs;
        return *this;
    }
};

inline Vec2 operator*(double s, const Vec2& v) {
    return {s * v.x, s * v.y};
}



inline double SqrMagnitude(const Vec2& v) {
    return Dot(v, v);
}

inline double Magnitude(const Vec2& v) {
    return sqrt(SqrMagnitude(v));
}

inline double Dot(const Vec2& v1, const Vec2& v2) {
    return v1.x * v2.x + v1.y * v2.y;
}

inline double Cross(const Vec2& v1, const Vec2& v2) {
    return v1.x * v2.y - v1.y *v2.x;
}

int main() {
    constexpr double tolerance = 0.00001f;

    Vec2 p0, p1, p2;

    cin >> p0.x >> p0.y >> p1.x >> p1.y;

    int n;
    cin >> n;

    auto p_01 = p1 - p0;

    for(int i = 0; i < n && cin >> p2.x >> p2.y; ++i) {
        auto p_02 = p2 - p0;        

        auto cross = Cross(p_01, p_02);

        if(cross > tolerance) {
            cout << "COUNTER_CLOCKWISE" << endl;
        }
        else if(cross < -tolerance){
            cout << "CLOCKWISE" << endl;
        }
        else {
            double normalizedDot {Dot(p_01.normalized(), p_02)};
            if(normalizedDot >= 0.0) {
                cout << (normalizedDot > Magnitude(p_01) ? "ONLINE_FRONT" : "ON_SEGMENT") << endl;
            }
            else {
                cout << "ONLINE_BACK" << endl;
            }

        }
    }

    return 0;
}

