#include <stdio.h>

int updown(double a[2], double b[2], double c[2]) 
{
    double dxab, dyab, dxac, dyac, ans;

    dxab = b[0] - a[0];
    dyab = b[1] - a[1];

    dxac = c[0] - a[0];
    dyac = c[1] - a[1];

    ans = dxab * dyac - dyab * dxac;

    if (ans > 0) {
        puts("COUNTER_CLOCKWIZE");
    } else if (ans < 0) {
        puts("CLOCKWIZE");
    } else {    //equal
        if ( dxab * dxac < 0 || dyab * dyac < 0) {
            puts("ONLINE_BACK");
        } else {
            if ( ((dxab * dxab) + (dyab * dyab)) <
                    ((dxac * dxac) + (dyac * dyac)) ) {
                puts("ONLINE_FRONT"); 
            } else {
                puts("ON_SEGMENT");
            }
        }
    }
    return ans;
}

int main(void)
{
    double a[2], b[2], tmp[2], ans;
    int n, i;

    for (i = 0; i < 2; i++) {
        scanf("%lf", &a[i]);
    }

    for (i = 0; i < 2; i++) {
        scanf("%lf", &b[i]);
    }

    scanf("%d",&n);

    for (i = 0; i < n; i++) {
        scanf("%lf%lf", &tmp[0], &tmp[1]);
        ans = updown(a, b, tmp);
    }

    return 0;
}

#include <stdio.h>
#include <math.h>
#include <float.h>

inline double cross_product_z(double v0x, double v0y, double vx, double vy) {
    return v0x * vy - v0y * vx;
}

int main() {
    int x, y, x0, y0, x1, y1;
    int q;
    double v0x, v0y, vx, vy, cp, cosa, denom, v0v, vv;
    const char *judge;
    const char *message[] = {
        "COUNTER_CLOCKWISE",
        "CLOCKWISE",
        "ONLINE_BACK",
        "ONLINE_FRONT",
        "ON_SEGMENT"
    };

    scanf("%d %d %d %d", &x0, &y0, &x1, &y1);
    scanf("%d", &q);

    v0x = x1 - x0;
    v0y = y1 - y0;

    while (q--) {
        scanf("%d %d", &x, &y);
        vx = x - x0;
        vy = y - y0;
        cp = cross_product_z(v0x, v0y, vx, vy);
        if (cp > 0) {
            judge = message[0];
        }
        else if (cp < 0) {
            judge = message[1];
        }
        else {
            v0v = v0x*v0x+v0y*v0y;
            vv = vx*vx+vy*vy;
            denom = sqrt(v0v) * sqrt(vv);
            if (denom < DBL_MIN * 10) {
                judge = message[4];
            }
            else {
                cosa = (v0x*vx + v0y*vy)/denom;
                if (cosa + 1 < DBL_MIN) {
                    judge = message[2];
                }
                else {
                    if (v0v < vv) {
                        judge = message[3];
                    }
                    else {
                        judge = message[4];
                    }
                }
            }

        }
        printf("%s\n", judge);
    }
}

#include <stdio.h>
#define DOT(v1, v2) (v1.x * v2.x + v1.y * v2.y)
#define CROSS(v1, v2) (v1.x * v2.y - v1.y * v2.x)
#define NORM(v) (v.x * v.x + v.y * v.y)

typedef struct { double x, y; } point_t;
typedef point_t vector_t;
typedef struct { point_t p0, p1; } segment_t;
typedef segment_t line_t;

static vector_t minus(vector_t v1, vector_t v2) {
  vector_t ret = { v1.x - v2.x, v1.y - v2.y };
  return ret;
}

static char *clockwise(segment_t s, point_t p) {
  vector_t a = minus(s.p1, s.p0);
  vector_t b = minus(p, s.p0);
  double c = CROSS(a, b);
  double d = DOT(a, b);
  double na = NORM(a);
  double nb = NORM(b);
  if (c > 0.0) return "COUNTER_CLOCKWISE";
  if (c < 0.0) return "CLOCKWISE";
  if (d < 0.0) return "ONLINE_BACK";
  if (nb > na) return "ONLINE_FRONT";
  return "ON_SEGMENT";
}

int main(int argc, char **argv) {
  int q, i;
  segment_t s;
  point_t p;

  scanf("%lf %lf %lf %lf", &s.p0.x, &s.p0.y, &s.p1.x, &s.p1.y);
  scanf("%d", &q);
  for (i = 0; i < q; ++i) {
    scanf("%lf %lf", &p.x, &p.y);
    printf("%s\n", clockwise(s, p));
  }
}

